import React, { Fragment, useState , useEffect } from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import { Card, CardContent , FormControl , Typography , InputLabel , Input , Select , MenuItem , Checkbox , ListItemText} from '@material-ui/core';


const styles = theme => ({
    formControl: {
        margin: theme.spacing(2),
        minWidth: 240,
        maxWidth: 240
    },
    title: {
        color: theme.palette.primary.light,
    }
  });

const MovieFilterCard = (props) =>{
    const { classes } = props;
    const [movieName , setMovieName] = useState("")
    const [genres , setGenres ]= useState([])
    const [genresList , setGenresList] = useState([])
    const [artists , setArtists] = useState([])
    const [artistsList , setArtistsList] = useState([])

    const movieNameChangeHandler = (event) => {
        setMovieName(event.target.value);
    }

    const genreSelectHandler = (event) => {
        setGenres(event.target.value);
    }

    const artistSelectHandler = (event) => {
        setArtists(event.target.value);
    }

    const releaseDateStartHandler = (event) => {
        this.setState({ releaseDateStart: event.target.value });
    }

    const releaseDateEndHandler = (event) => {
        this.setState({ releaseDateEnd: event.target.value });
    }
    
    //fetch Genres
    async function fetchGenres(){
        try {
        
            const rawResponse = await fetch("http://localhost:8085/api/v1/genres",{
                method:"GET",
                headers:{
                    "Accept" : "application/json;charset=UTF-8",
                    'Content-Type' : 'application/json;charset=UTF-8'
                }
            })
    
            const result = await rawResponse.json()        
            
            if(rawResponse.ok){
                setGenresList(result.genres)
            }else{
                const error = new Error();
                error.message = result.message || "Something went wrong"
            }
            
        } catch (error) {
            alert(`Error: ${error.message}`)     
        }
    }

    //fetch Artists
    async function fetchArtist(){
        try {
        
            const rawResponse = await fetch("http://localhost:8085/api/v1/artists?page=1",{
                method:"GET",
                headers:{
                    "Accept" : "application/json;charset=UTF-8",
                    'Content-Type' : 'application/json;charset=UTF-8'
                }
            })
    
            const result = await rawResponse.json()        
            
            if(rawResponse.ok){
                setArtistsList(result.artists)
            }else{
                const error = new Error();
                error.message = result.message || "Something went wrong"
            }
            
        } catch (error) {
            alert(`Error: ${error.message}`)
            
        }

    }


    useEffect(()=>{
        fetchGenres()
        fetchArtist()
    },[])
    

    return(
        <Fragment>
            <Card>
                <CardContent>
                    <FormControl className={classes.formControl}>
                        <Typography className={classes.title} color="textSecondary">
                            FIND MOVIES BY:
                        </Typography>
                    </FormControl>

                    <FormControl className={classes.formControl}>
                        <InputLabel htmlFor="movieName">Movie Name</InputLabel>
                        <Input id="movieName" onChange={movieNameChangeHandler} />
                    </FormControl>

                    <FormControl className={classes.formControl}>
                        <InputLabel htmlFor="select-multiple-checkbox">Genres</InputLabel>
                        <Select
                            multiple
                            input={<Input id="select-multiple-checkbox-genre" />}
                            renderValue={selected => selected.join(',')}
                            value={genres}
                            onChange={genreSelectHandler}
                        >
                            {genresList.map(genre => (
                                <MenuItem key={genre.id} value={genre.genre}>
                                    <Checkbox checked={genres.indexOf(genre.genre) > -1} />
                                    <ListItemText primary={genre.genre} />
                                </MenuItem>
                            ))}
                        </Select>
                    </FormControl>

                    <FormControl className={classes.formControl}>
                        <InputLabel htmlFor="select-multiple-checkbox">Artists</InputLabel>
                        <Select
                            multiple
                            input={<Input id="select-multiple-checkbox" />}
                            renderValue={selected => selected.join(',')}
                            value={artists}
                            onChange={artistSelectHandler}
                        >
                            {artistsList.map(artist => (
                                <MenuItem key={artist.id} value={artist.first_name + " " + artist.last_name}>
                                    <Checkbox checked={artists.indexOf(artist.first_name + " " + artist.last_name) > -1} />
                                    <ListItemText primary={artist.first_name + " " + artist.last_name} />
                                </MenuItem>
                            ))}
                        </Select>
                    </FormControl>

                    <FormControl className={classes.formControl}>
                                    <TextField
                                        id="releaseDateStart"
                                        label="Release Date Start"
                                        type="date"
                                        defaultValue=""
                                        InputLabelProps={{ shrink: true }}
                                        onChange={this.releaseDateStartHandler}
                                    />
                                </FormControl>

                                <FormControl className={classes.formControl}>
                                    <TextField
                                        id="releaseDateEnd"
                                        label="Release Date End"
                                        type="date"
                                        defaultValue=""
                                        InputLabelProps={{ shrink: true }}
                                        onChange={this.releaseDateEndHandler}
                                    />
                                </FormControl>
                                <br /><br />

                </CardContent>
            </Card>
        </Fragment>
    )
}

MovieFilterCard.propTypes = {
    classes: PropTypes.object.isRequired,
  };


export default withStyles(styles)(MovieFilterCard)
