import { Fragment } from 'react';
import Header from '../../common/header/Header';
import './Home.css'
import MovieFilterCard from './MovieFilterCard';
import ReleasedMovies from './ReleasedMovies';
import React, { Component, useEffect, useState } from 'react';
import { ImageList , ImageListItem , ImageListItemBar } from '@material-ui/core';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';

const styles = theme => ({
    root: {
      display: 'flex',
      flexWrap: 'wrap',
      justifyContent: 'space-around',
      overflow: 'hidden',
      backgroundColor: theme.palette.background.paper,
    },
    gridList: {
        flexWrap: 'nowrap',
        transform: 'translateZ(0)',
        width: '100%'
    },
    title: {
      color: theme.palette.primary.light,
    },
    titleBar: {
      background:
        'linear-gradient(to top, rgba(0,0,0,0.7) 0%, rgba(0,0,0,0.3) 70%, rgba(0,0,0,0) 100%)',
    }, 
    gridListMain: {
        transform: 'translateZ(0)',
        cursor: 'pointer'
    },  
    formControl: {
        margin: theme.spacing(2),
        minWidth: 240,
        maxWidth: 240
    },
    
  });


const Home1 = (props) => {

    const { classes } = props;
    const [movies , setMovies] = useState([]);
    const [releasedMovies , setReleasedMovies] = useState([]);

    useEffect(()=>{
        loadUpcomingMovies()
        loadReleasedMovies()
    },[])

    async function loadUpcomingMovies(){
        try {
        
            const rawResponse = await fetch("http://localhost:8085/api/v1/movies?page=1&limit=6&status=PUBLISHED",{
                method:"GET",
                headers:{
                    "Accept" : "application/json;charset=UTF-8",
                    'Content-Type' : 'application/json;charset=UTF-8'
                }
            })
    
            const result = await rawResponse.json()
            
            
            if(rawResponse.ok){
                setMovies(result.movies)
            }else{
                const error = new Error();
                error.message = result.message || "Something went wrong"
            }
            
        } catch (error) {
            alert(`Error: ${error.message}`)
            
        }
    }

    async function loadReleasedMovies(){
        try {
        
            const rawResponse = await fetch("http://localhost:8085/api/v1/movies?page=1&status=RELEASED",{
                method:"GET",
                headers:{
                    "Accept" : "application/json;charset=UTF-8",
                    'Content-Type' : 'application/json;charset=UTF-8'
                }
            })
    
            const result = await rawResponse.json()        
            
            if(rawResponse.ok){
                setReleasedMovies(result.movies)
            }else{
                const error = new Error();
                error.message = result.message || "Something went wrong"
            }
            
        } catch (error) {
            alert(`Error: ${error.message}`)
            
        }
    }
    
    

    return <Fragment>
        <Header></Header>
        <h3 className='upcoming-movies-header'>Upcoming movies</h3>
        <div className='classes.root'>
            <ImageList cols={5} className={classes.gridList}>
                {movies.map(movie => (
                <ImageListItem key={movie.poster_url} className='classes.title'>
                    <img src={movie.poster_url} alt={movie.title}/>
                    <ImageListItemBar
                    title={movie.title}
                    />
                </ImageListItem>
                ))}
            </ImageList>

        </div>
        <div style={{display:'flex'}}>
            <div style={{width: '76%' , margin: '16px'}}>
            <Fragment>
                <ImageList rowHeight={350} cols={4} className={classes.gridListMain}>
                    {movies.map(movie => (
                    <ImageListItem key={movie.poster_url} className='classes.title' style={{margin:'15px'}}>
                        <img src={movie.poster_url} alt={movie.title}/>
                        <ImageListItemBar
                            title={movie.title}
                            subtitle={`Release Date ${movie.release_date}`}
                        />
                    </ImageListItem>
                    ))}
                </ImageList>
            </Fragment>
            </div>
            <div style={{width: '24%' , margin: '16px'}}>
                <MovieFilterCard baseUrl={props.baseUrl}></MovieFilterCard>
            </div>
        </div>
        </Fragment>
}

Home1.propTypes = {
    classes: PropTypes.object.isRequired,
  };

export default withStyles(styles)(Home1);
