import { withStyles } from '@mui/material';
import React, { Component, Fragment } from 'react';

const styles = theme => ({
    formControl: {
        margin: theme.spacing.unit,
        minWidth: 240,
        maxWidth: 240
    },
    title: {
        color: theme.palette.primary.light,
    }
  });



const MovieFilterCard = () =>{
    const { classes } = props;
    return(
        <Fragment>
            <Card>
                <CardContent>
                    <FormControl className={classes.formControl}>
                        <Typography className={classes.title} color="textSecondary">
                            FIND MOVIES BY:
                        </Typography>
                    </FormControl>

                    <FormControl className={classes.formControl}>
                        <InputLabel htmlFor="movieName">Movie Name</InputLabel>
                        <Input id="movieName" onChange={this.movieNameChangeHandler} />
                    </FormControl>

                    <FormControl className={classes.formControl}>
                        <InputLabel htmlFor="select-multiple-checkbox">Genres</InputLabel>
                        <Select
                            multiple
                            input={<Input id="select-multiple-checkbox-genre" />}
                            renderValue={selected => selected.join(',')}
                            value={this.state.genres}
                            onChange={this.genreSelectHandler}
                        >
                            {this.state.genresList.map(genre => (
                                <MenuItem key={genre.id} value={genre.genre}>
                                    <Checkbox checked={this.state.genres.indexOf(genre.genre) > -1} />
                                    <ListItemText primary={genre.genre} />
                                </MenuItem>
                            ))}
                        </Select>
                    </FormControl>

                    <FormControl className={classes.formControl}>
                        <InputLabel htmlFor="select-multiple-checkbox">Artists</InputLabel>
                        <Select
                            multiple
                            input={<Input id="select-multiple-checkbox" />}
                            renderValue={selected => selected.join(',')}
                            value={this.state.artists}
                            onChange={this.artistSelectHandler}
                        >
                            {this.state.artistsList.map(artist => (
                                <MenuItem key={artist.id} value={artist.first_name + " " + artist.last_name}>
                                    <Checkbox checked={this.state.artists.indexOf(artist.first_name + " " + artist.last_name) > -1} />
                                    <ListItemText primary={artist.first_name + " " + artist.last_name} />
                                </MenuItem>
                            ))}
                        </Select>
                    </FormControl>

                    <FormControl className={classes.formControl}>
                        <TextField
                            id="releaseDateStart"
                            label="Release Date Start"
                            type="date"
                            defaultValue=""
                            InputLabelProps={{ shrink: true }}
                            onChange={this.releaseDateStartHandler}
                        />
                    </FormControl>

                    <FormControl className={classes.formControl}>
                        <TextField
                            id="releaseDateEnd"
                            label="Release Date End"
                            type="date"
                            defaultValue=""
                            InputLabelProps={{ shrink: true }}
                            onChange={this.releaseDateEndHandler}
                        />
                    </FormControl>
                    <br /><br />
                    <FormControl className={classes.formControl}>
                        <Button onClick={() => this.filterApplyHandler()} variant="contained" color="primary">
                            APPLY
                        </Button>
                    </FormControl>
                </CardContent>
            </Card>

        </Fragment>
    )
}

export default withStyles(styles)(MovieFilterCard)
