import React, { Fragment, useState } from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import { Card, CardContent , FormControl , Typography} from '@material-ui/core';


const styles = theme => ({
    formControl: {
        margin: theme.spacing(2),
        minWidth: 240,
        maxWidth: 240
    },
    title: {
        color: theme.palette.primary.light,
    }
  });

const MovieFilterCard = (props) =>{
    const { classes } = props;
    const {movieNameState , setMovieNameState} = useState()

    movieNameChangeHandler = event => {
        this.setState({ movieName: event.target.value });
    }

    return(
        <Fragment>
            <Card>
                <CardContent>
                    <FormControl className={classes.formControl}>
                        <Typography className={classes.title} color="textSecondary">
                            FIND MOVIES BY:
                        </Typography>
                    </FormControl>

                    <FormControl className={classes.formControl}>
                        <InputLabel htmlFor="movieName">Movie Name</InputLabel>
                        <Input id="movieName" onChange={this.movieNameChangeHandler} />
                    </FormControl>
                </CardContent>
            </Card>
        </Fragment>
    )
}

MovieFilterCard.propTypes = {
    classes: PropTypes.object.isRequired,
  };


export default withStyles(styles)(MovieFilterCard)
