import React, { Component, useEffect, useState } from 'react';
import { Fragment } from 'react';
import { ImageList , ImageListItem , ImageListItemBar } from '@material-ui/core';
import IconButton from '@material-ui/core/IconButton';
import StarBorderIcon from '@material-ui/icons/StarBorder';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import './Home.css'

const styles = theme => ({
    root: {
      display: 'flex',
      flexWrap: 'wrap',
      justifyContent: 'space-around',
      overflow: 'hidden',
      backgroundColor: theme.palette.background.paper,
    },
    gridListMain: {
        transform: 'translateZ(0)',
        cursor: 'pointer'
    },
    title: {
      color: theme.palette.primary.light,
    },
    titleBar: {
      background:
        'linear-gradient(to top, rgba(0,0,0,0.7) 0%, rgba(0,0,0,0.3) 70%, rgba(0,0,0,0) 100%)',
    },
  });

const ReleasedMovies = (props) => {

    const [movies , setMovies] = useState([]);
    const { classes } = props;

    useEffect(()=>{
        loadReleasedMovies()
    },[])

    async function loadReleasedMovies(){
        try {
        
            const rawResponse = await fetch("http://localhost:8085/api/v1/movies?page=1&status=RELEASED",{
                method:"GET",
                headers:{
                    "Accept" : "application/json;charset=UTF-8",
                    'Content-Type' : 'application/json;charset=UTF-8'
                }
            })
    
            const result = await rawResponse.json()
            
            
            if(rawResponse.ok){
                console.log(result.movies)
                setMovies(result.movies)
            }else{
                const error = new Error();
                error.message = result.message || "Something went wrong"
            }
            
        } catch (error) {
            alert(`Error: ${error.message}`)
            
        }
    

    }
    

    return(
        
        <div className='classes.root'>
            <ImageList cellHeight={350} cols={4} className={classes.gridListMain}>
                {movies.map(movie => (
                <ImageListItem key={movie.poster_url} className='classes.title'>
                    <img src={movie.poster_url} alt={movie.title} />
                    <ImageListItemBar
                    title={movie.title}
                    actionIcon={
                        <IconButton>
                        <StarBorderIcon className={movie.title} />
                        </IconButton>
                    }
                    />
                </ImageListItem>
                ))}
            </ImageList>

        </div>
    )
}

ReleasedMovies.propTypes = {
    classes: PropTypes.object.isRequired,
  };
export default withStyles(styles)(ReleasedMovies);
