import React, { Fragment, useState } from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import { Card, CardContent , FormControl , Typography , InputLabel , Input , Select , MenuItem , Checkbox , ListItemText} from '@material-ui/core';


const styles = theme => ({
    formControl: {
        margin: theme.spacing(2),
        minWidth: 240,
        maxWidth: 240
    },
    title: {
        color: theme.palette.primary.light,
    }
  });

const MovieFilterCard = (props) =>{
    const { classes } = props;
    const {movieName , setMovieName} = useState("")
    const {genres , setGenres} = useState([])
    const {genresList , setGenresList} = useState([])

    const movieNameChangeHandler = (event) => {
        setMovieName(event.target.value);
    }

    const genreSelectHandler = (event) => {
        setGenres(event.target.value);
    }

    async function fetchGenres(){
        try {
        
            const rawResponse = await fetch("http://localhost:8085/api/v1/genres",{
                method:"GET",
                headers:{
                    "Accept" : "application/json;charset=UTF-8",
                    'Content-Type' : 'application/json;charset=UTF-8'
                }
            })
    
            const result = await rawResponse.json()        
            
            if(rawResponse.ok){
                setMovies(result.movies)
            }else{
                const error = new Error();
                error.message = result.message || "Something went wrong"
            }
            
        } catch (error) {
            alert(`Error: ${error.message}`)
            
        }

    }

    useEffect(()=>{
        fetchGenres()
    },[])
    

    return(
        <Fragment>
            <Card>
                <CardContent>
                    <FormControl className={classes.formControl}>
                        <Typography className={classes.title} color="textSecondary">
                            FIND MOVIES BY:
                        </Typography>
                    </FormControl>

                    <FormControl className={classes.formControl}>
                        <InputLabel htmlFor="movieName">Movie Name</InputLabel>
                        <Input id="movieName" onChange={movieNameChangeHandler} />
                    </FormControl>

                    <FormControl className={classes.formControl}>
                        <InputLabel htmlFor="select-multiple-checkbox">Genres</InputLabel>
                        <Select
                            multiple
                            input={<Input id="select-multiple-checkbox-genre" />}
                            renderValue={selected => selected.join(',')}
                            value={genres}
                            onChange={genreSelectHandler}
                        >
                            {genresList.map(genre => (
                                <MenuItem key={genre.id} value={genre.genre}>
                                    <Checkbox checked={genres.indexOf(genre.genre) > -1} />
                                    <ListItemText primary={genre.genre} />
                                </MenuItem>
                            ))}
                        </Select>
                    </FormControl>

                </CardContent>
            </Card>
        </Fragment>
    )
}

MovieFilterCard.propTypes = {
    classes: PropTypes.object.isRequired,
  };


export default withStyles(styles)(MovieFilterCard)
