import { Box , Tab } from "@mui/material";
import { TabContext ,TabList , TabPanel } from "@mui/lab";
import React, { Component } from 'react';
import { useState } from "react";
import { TextValidator, ValidatorForm } from 'react-material-ui-form-validator';
import { Button } from '@material-ui/core';

const MuiTabs = () =>{

    const [value , setValue] = useState('1')
    const [signUpState , setSignUpState] = useState({
        first_name : '',
        last_name : '',
        email_address : '',
        mobile_number : '',
        password : '',
    })
    

    let first_name = signUpState.first_name;
    let last_name = signUpState.last_name;
    let email_address = signUpState.email_address;
    let mobile_number = signUpState.mobile_number;
    let password = signUpState.password;

    async function handleLoginSubmit(email_address,password){
        const params = window.btoa(`${email_address.value}:${password.value}`)
        const rawResponse = await fetch("http://localhost:8085/api/v1/auth/login",{
            method:"POST",
            headers:{
            'Accept':"application/json;charset=UTF-8",
            'Content-Type' : 'application/json;charset=UTF-8',
            authorization : `Basic ${params}`
        }
        })

        const result = await rawResponse.json()
        
        if(rawResponse.ok){
            console.log("Success")
            
        }
    }

    const loginChangedHandler = (e) => {
        const state = signUpState;
        state[e.target.name] = e.target.value;
        setSignUpState({...state});
    }


    const handleChange = (event, newValue) => {
        setValue(newValue);
    };

    const inputChangedHandler = (e) => {
        const state = signUpState;
        state[e.target.name] = e.target.value;
        setSignUpState({...state});
    }

    const handleSignUpSubmit = (e) => {
        e.preventDefault();
        signUpHandler(signUpState);
        setSignUpState({
        first_name : '',
        last_name : '',
        email_address : '',
        mobile_number : '',
        password : '',
        });

    }

    async function signUpHandler(newSubscriber){
                

        const rawResponse = await fetch("http://localhost:8085/api/v1/signup" , {
            method:"POST",
            headers:{
            'Accept':"application/json;charset=UTF-8",
            'Content-Type' : 'application/json;charset=UTF-8'
        },body:JSON.stringify(newSubscriber)
        })

        const data = rawResponse.json();
        if(rawResponse.ok){ 
        console.log("Success")
        }
    }


    return (
        <Box>
            <TabContext value = {value}>
                <Box sx = {{borderBottom: 1, borderColor: 'pink'}}>
                    <TabList aria-label="Tabs example" onChange={handleChange}>
                        <Tab label = 'Login' value = '1' />
                        <Tab label = 'Register' value = '2' />
                    </TabList>
                </Box>
                <TabPanel value="1">
                    <ValidatorForm className='login-form-container'>
                        <TextValidator
                            id ='name'
                            label = 'Username*'
                            type = 'text'
                            name = 'name'
                            validators={['required']}
                            errorMessages={['Username cannot be empty']}
                            onChange = {loginChangedHandler}
                            style={{left:'25%'}}
                        >
                        </TextValidator><br/>
                        <TextValidator
                            id ='password'
                            validators={['required']}
                            errorMessages={['Password cannot be empty']}
                            label="Password*"
                            name="password"
                            type="password" 
                            onChange = {loginChangedHandler}
                            style={{left:'25%'}}  
                        >
                        </TextValidator><br/><br/>
                        <Button type='button' style={{left:'37%',color:'white',backgroundColor:'rgb(83, 102, 249)'}} onclick={handleLoginSubmit}>Login</Button>
                    </ValidatorForm>
                </TabPanel>
                <TabPanel value="2">
                    <ValidatorForm className='signUp-form-container' onSubmit={handleSignUpSubmit}>
                        <TextValidator
                                id ='first_name'
                                label = 'First Name *'
                                type = 'text'
                                name = 'first_name'
                                validators={['required']}
                                errorMessages={'required'}
                                onChange={inputChangedHandler}
                                value = {first_name}
                                style={{left:'25%'}}
                        >
                        </TextValidator>
                        <TextValidator
                            id ='last_name'
                            label = 'Last Name *'
                            type = 'text'
                            name = 'last_name'
                            validators={['required']}
                            errorMessages={'required'}
                            onChange={inputChangedHandler}
                            value = {last_name}
                            style={{left:'25%'}}
                        >
                        </TextValidator>
                        <TextValidator
                            id ='email_address'
                            label = 'Email *'
                            type = 'text'
                            name = 'email_address'
                            validators={['required' , 'isEmail']}
                            errorMessages={['required','only email allowed']}
                            onChange={inputChangedHandler}
                            value = {email_address}
                            style={{left:'25%'}}
                        >
                        </TextValidator>
                        <TextValidator
                            id ='password'
                            label = 'Password *'
                            type = 'password'
                            name = 'password'
                            validators={['required']}
                            errorMessages={'required'}
                            onChange={inputChangedHandler}
                            value = {password}
                            style={{left:'25%'}}
                        >
                        </TextValidator>
                        <TextValidator
                            id ='mobile_number'
                            label = 'Contact No *'
                            type = 'number'
                            name = 'mobile_number'
                            validators={['required']}
                            errorMessages={'required'}
                            onChange={inputChangedHandler}
                            value = {mobile_number}
                            style={{left:'25%'}}
                        >
                        </TextValidator><br/><br/>
                        <Button type="submit" style={{left:'37%',color:'white',backgroundColor:'rgb(83, 102, 249)'}}>Register</Button>
                    </ValidatorForm>
                </TabPanel>
            </TabContext>
        </Box>
    )

}

export default MuiTabs;
