import React, { Component } from 'react';
import { Fragment } from 'react';
import Header from '../../common/header/Header';
import './Home.css'
import MovieFilterCard from './MovieFilterCard';
import ReleasedMovies from './ReleasedMovies';

import UpcomingMovies from './UpcomingMovies';

const styles = theme => ({
    root: {
      display: 'flex',
      flexWrap: 'wrap',
      justifyContent: 'space-around',
      overflow: 'hidden',
      backgroundColor: theme.palette.background.paper,
    },
    gridList: {
        flexWrap: 'nowrap',
        transform: 'translateZ(0)',
        width: '100%'
    },
    title: {
      color: theme.palette.primary.light,
    },
    titleBar: {
      background:
        'linear-gradient(to top, rgba(0,0,0,0.7) 0%, rgba(0,0,0,0.3) 70%, rgba(0,0,0,0) 100%)',
    }, 
    gridListMain: {
        transform: 'translateZ(0)',
        cursor: 'pointer'
    },    formControl: {
        margin: theme.spacing(2),
        minWidth: 240,
        maxWidth: 240
    },
    
  });


const Home1 = (props) => {

    

    return <Fragment>
        <Header></Header>
        <h3 className='upcoming-movies-header'>Upcoming movies</h3>
        <UpcomingMovies></UpcomingMovies>
        <div style={{display:'flex'}}>
            <div style={{width: '76%' , margin: '16px'}}>
                <ReleasedMovies></ReleasedMovies>
            </div>
            <div style={{width: '24%' , margin: '16px'}}>
                <MovieFilterCard baseUrl={props.baseUrl}></MovieFilterCard>
            </div>
        </div>
        </Fragment>
}

export default Home1;
