import React, { Component } from 'react';
import { Fragment } from 'react';
import Header from '../../common/header/Header';
import './Home.css'
import MovieFilterCard from './MovieFilterCard';
import ReleasedMovies from './ReleasedMovies';

import UpcomingMovies from './UpcomingMovies';


const Home = (props) => {

    filterApplyHandler = (movieName , genres , artists , releaseDateStart , releaseDateEnd) => {
        let queryString = "?status=RELEASED";
        if (movieName !== "") {
            queryString += "&title=" + this.state.movieName;
        }
        if (genres.length > 0) {
            queryString += "&genres=" + genres.toString();
        }
        if (artists.length > 0) {
            queryString += "&artists=" + artists.toString();
        }
        if (releaseDateStart !== "") {
            queryString += "&start_date=" + releaseDateStart;
        }
        if (releaseDateEnd !== "") {
            queryString += "&end_date=" + releaseDateEnd;
        }
    }

    return <Fragment>
        <Header></Header>
        <h3 className='upcoming-movies-header'>Upcoming movies</h3>
        <UpcomingMovies></UpcomingMovies>
        <div style={{display:'flex'}}>
            <div style={{width: '76%' , margin: '16px'}}>
                <ReleasedMovies></ReleasedMovies>
            </div>
            <div style={{width: '24%' , margin: '16px'}}>
                <MovieFilterCard baseUrl={props.baseUrl}></MovieFilterCard>
            </div>
        </div>
        </Fragment>
}

export default Home;
