import { Box , Tab } from "@mui/material";
import { TabContext ,TabList , TabPanel } from "@mui/lab";
import React, { Component } from 'react';
import { useState } from "react";

const MuiTabs = () =>{
    const [value , setValue] = useState('1')

    const handleChange = (e , newValue) =>{
        setValue(newValue)
    }

    return (
        <Box>
            <TabContext value = {value}>
                <Box sx = {{borderBottom: 1, borderColor: 'pink'}}>
                    <TabList aria-label="Tabs example" onChange={handleChange}>
                        <Tab label = 'Login' value = '1' />
                        <Tab label = 'Register' value = '2' />
                    </TabList>
                </Box>
                <TabPanel value="1">
                <ValidatorForm className='login-form-container' onSubmit={handleLoginSubmit}>
          <TextValidator
              id ='name'
              label = 'Username*'
              type = 'text'
              name = 'name'
              validators={['required']}
              errorMessages={['Username cannot be empty']}
              style={{left:'25%'}}
          >
          </TextValidator><br/>
          <TextValidator
              id ='password'
              validators={['required']}
              errorMessages={['Password cannot be empty']}
              label="Password*"
              name="password"
              type="password"  
              style={{left:'25%'}}  
          >
          </TextValidator><br/><br/>
          <Button type='Submit' style={{left:'37%',color:'white',backgroundColor:'rgb(83, 102, 249)'}}>Login</Button>
        </ValidatorForm>
                </TabPanel>
                <TabPanel value="2">Register</TabPanel>
            </TabContext>
        </Box>
    )

}

export default MuiTabs;
